
-- Partial Implementation of Change TRacking
-- DOES NOT implement CT on all tables, only selected tables.

USE KenticoCMS_1;
GO
IF EXISTS ( SELECT
                   name
            FROM sys.procedures
            WHERE
                   name = 'PrintImmediate'
) 
    BEGIN
        DROP PROCEDURE
             PrintImmediate;
    END;
GO

CREATE PROCEDURE PrintImmediate (
       @MSG AS NVARCHAR (MAX) 
) 
AS
BEGIN
    RAISERROR ( @MSG , 10 , 1
    )WITH NOWAIT;
END;
GO
IF EXISTS ( SELECT
                   name
            FROM sys.procedures
            WHERE
                   name = 'proc_TurnOnChangeTracking'
) 
    BEGIN
        DROP PROCEDURE
             proc_TurnOnChangeTracking;
    END;
GO
-- exec proc_TurnOnChangeTracking 'KenticoCMS_1'
CREATE PROCEDURE proc_TurnOnChangeTracking (
       @DBNAME AS NVARCHAR (250) 
) 
AS
BEGIN
    BEGIN
        BEGIN TRY
            DECLARE
                   @MySql AS NVARCHAR ( MAX
                   ) = '';
            SET @MySql = ' ALTER DATABASE ' + @DBNAME + ' SET CHANGE_TRACKING = ON (CHANGE_RETENTION = 2 DAYS , AUTO_CLEANUP = ON) ';
            EXEC ( @MySql
            );
        END TRY
        BEGIN CATCH
            PRINT 'Verify change tracking is turned on for ' + @DBNAME;
        END CATCH;
        EXEC PrintImmediate 'Table: SM_TwitterApplication';
        IF EXISTS ( SELECT
                           name
                    FROM sys.tables
                    WHERE
                           name = 'SM_TwitterApplication'
        ) 
            BEGIN
                IF NOT EXISTS ( SELECT
                                       sys.tables.name AS Table_name
                                FROM
                                sys.change_tracking_tables
                                JOIN sys.tables
                                ON
                                       sys.tables.object_id = sys.change_tracking_tables.object_id
                                JOIN sys.schemas
                                ON
                                       sys.schemas.schema_id = sys.tables.schema_id
                                WHERE
                                       sys.tables.name = 'SM_TwitterApplication' AND
                                       sys.schemas.name = 'dbo'
                ) 
                    BEGIN
                        IF NOT EXISTS ( SELECT
                                               CONSTRAINT_NAME
                                        FROM information_schema.TABLE_CONSTRAINTS
                                        WHERE
                                               CONSTRAINT_TYPE = 'PRIMARY KEY' AND
                                               TABLE_NAME = 'SM_TwitterApplication'
                        ) 
                            BEGIN
                                ALTER TABLE SM_TwitterApplication
                                ADD
                                            SurrogateKey_SM_TwitterApplication BIGINT IDENTITY ( 1 , 1
                                            );
                                CHECKPOINT;
                                ALTER TABLE dbo.SM_TwitterApplication
                                ADD
                                            CONSTRAINT PKI_SM_TwitterApplication PRIMARY KEY CLUSTERED ( SurrogateKey_SM_TwitterApplication
                                            );
                                CHECKPOINT;
                            END;
                        ALTER TABLE DBO.SM_TwitterApplication ENABLE CHANGE_TRACKING
                                WITH ( TRACK_COLUMNS_UPDATED = ON
